  function reverseCoordinate(coor) {
    let retCoor = [];
    if (coor !== undefined && coor !== null) {
      if (coor.length > 0) {
        for (let coorIdx = 0; coorIdx < coor.length; coorIdx++) {
          const bound = coor[coorIdx];
          let row = [];
          if (bound.length === 2) {
            row.push(bound[1]);
            row.push(bound[0]);
            retCoor.push(row);
          }
        }

      }
    }
    return retCoor;
  }


